package baidumapsdk.demo.asmewill;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.ArrayList;import java.util.List;import baidumapsdk.demo.R;import butterknife.BindView;import butterknife.ButterKnife;/** * Created by huangbo on 17/2/8. */public class CityAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder> {    private final int TITLE_TYPE = 1;    private final int CONTENT_TYPE = 2;    private List<CityChildBean> mList = new ArrayList<>();    private OnItemOnClickListener mOnItemOnClickListener;    public CityAdapter(List<CityChildBean>  list) {        this.mList = list;    }    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View tv_ItemView = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_city_title, parent, false);        View iv_ItemView = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_city_content, parent, false);        RecyclerView.ViewHolder holder = null;        switch (viewType) {            case TITLE_TYPE:                holder = new TitleView(tv_ItemView);                break;            case CONTENT_TYPE:                holder = new ContentView(iv_ItemView);                break;        }        return holder;    }    @Override    public void onBindViewHolder(final RecyclerView.ViewHolder holder, final int position) {        if (getItemViewType(position) == TITLE_TYPE) {            ((TitleView) holder).mTitle.setText(mList.get(position).getCity());        } else if (getItemViewType(position) == CONTENT_TYPE) {            ((ContentView) holder).mCity.setText(mList.get(position).getCity());            holder.itemView.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View v) {                    if(mOnItemOnClickListener!=null){                        mOnItemOnClickListener.onClick(position,holder.itemView);                    }                }            });        }    }    @Override    public int getItemCount() {        return mList.size();    }    @Override    public int getItemViewType(int position) {        if (mList.get(position).getCity().length() > 1)            return CONTENT_TYPE;        else            return TITLE_TYPE;    }    public class TitleView extends RecyclerView.ViewHolder {        @BindView(R.id.tv_title)        TextView mTitle;        public TitleView(View itemView) {            super(itemView);            ButterKnife.bind(this, itemView);        }    }    public class ContentView extends RecyclerView.ViewHolder {        @BindView(R.id.tv_city)        TextView mCity;        public ContentView(View itemView) {            super(itemView);            ButterKnife.bind(this, itemView);        }    }    public void setmOnItemOnClickListener(OnItemOnClickListener onItemOnClickListener) {        if (onItemOnClickListener != null) {            mOnItemOnClickListener = onItemOnClickListener;        }    }    public interface OnItemOnClickListener {        void onClick(int position, View view);    }}