package baidumapsdk.demo.asmewill;import com.baidu.location.LocationClient;import com.baidu.location.LocationClientOption;import com.baidu.mapapi.map.BaiduMap;/** * Created by Administrator on 2017/6/17. */public class MapUtils {    /**     * 初始化地图     */    public static void initMap(LocationClient mLocationClient, int span){        LocationClientOption option = new LocationClientOption();        option.setLocationMode(LocationClientOption.LocationMode.Hight_Accuracy);        option.setCoorType("bd09ll");        option.setScanSpan(span);        option.setIsNeedAddress(true);        option.setOpenGps(true);        option.setLocationNotify(true);        option.setIsNeedLocationDescribe(true);        option.setIsNeedLocationPoiList(true);        option.setIgnoreKillProcess(false);        option.SetIgnoreCacheException(false);        option.setEnableSimulateGps(false);        mLocationClient.setLocOption(option);    }    public  static void  initMap(BaiduMap map,int mapType,boolean isTrafficEnable){        map.setMapType(mapType);        map.setTrafficEnabled(isTrafficEnable);    }}