package baidumapsdk.demo.asmewill;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import baidumapsdk.demo.BMapApiDemoMain;import baidumapsdk.demo.R;import butterknife.BindView;import butterknife.ButterKnife;/** * Created by Administrator on 2017/10/26 0026. */public class MySelectAddressDemo extends Activity implements View.OnClickListener{    @BindView(R.id.btn_my_demo)    Button btn_my_demo;    @BindView(R.id.btn_api_demo)    Button btn_api_demo;    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_select_address);        ButterKnife.bind(this);        btn_my_demo.setOnClickListener(this);        btn_api_demo.setOnClickListener(this);    }    @Override    public void onClick(View v) {        switch (v.getId()){            case R.id.btn_my_demo:                break;            case R.id.btn_api_demo:                startActivity(new Intent(this, BMapApiDemoMain.class));                break;        }    }}